mapper.select:
select de.*,DATE_FORMAT(de.created,'%Y-%m-%d') as created,c.name as departmentName,e1.name as sysName,e2.name as equipmentName,ee2.name as claimantName, ee1.name as createdByName,ee3.name as confirmer1Name from defect de left join company c on c.id=de.departmentId left join equipment e1 on e1.id=de.sysId left join equipment e2 on e2.id=de.equipmentId left join employee ee1 on ee1.id=de.createdBy left join employee ee2 on ee2.id=de.claimant left join employee ee3 on ee3.id=de.confirmer1
and de.departmentId=#{departmentId}
and de.created like #{date}
and (de.type=1 or de.type=7) and (de.type=5 or de.type=2 or de.type=3)
and de.empIds like concat("%",#{employeeId},"%")
order by de.created desc

select * from scrdaily

    and project_id =#{departmentId}
    and datetime like #{date}
